INSERT INTO axe_thematic(axe_thematic_nom, axe_thematic_desc) VALUES ('Étalement urbain', 'Observer le développement des surfaces urbanisées en périphérie des villes dans un objectif de limiter les phénomènes de mitage et de développement linéaire de l’urbanisation, pour une meilleure gestion de la consommation de l’espace, la recherche de la forme urbaine, et notamment le maintien la silhouette traditionnelle des bourgs, la lutte contre la standardisation des paysages. zones pavillonnaires, mitage, infrastructures, limites urbaines, écoquartier, coupures urbaines');
INSERT INTO axe_thematic(axe_thematic_nom, axe_thematic_desc) VALUES ('Centres bourgs et espaces publics', 'Observer l’évolution des centre-bourgs qui doivent rester attractifs et conserver leur fonction centrale notamment par le développement de lieux de vie collective, et d’espaces de nature en ville. La densification de ces centres est aussi un objectif de la bonne gestion de la consommation de l’espace. Places, espaces publics, restauration de façades, réhabilitation, nature en ville, embellissement, friches, densification, réseaux, centre ancien, vacance');
INSERT INTO axe_thematic(axe_thematic_nom, axe_thematic_desc) VALUES ('Zones d’activité', 'Observer les zones d’activité existantes, les extensions et créations de nouvelles zones, à la fois dans leur implantation, leur architecture, et leur intégration dans le territoire. Les liaisons avec les secteurs habités sont aussi en question. Extension, création, réhabilitation, friches, infrastructures');
INSERT INTO axe_thematic(axe_thematic_nom, axe_thematic_desc) VALUES ('Habitat récent', 'Observer à l’échelle de la parcelle, en centre bourg ou zone périurbaine, la qualité de l’architecture et de l’implantation d’un bâti récent aux vues du bâti traditionnel, pour une intégration des formes architecturales dans leur environnement. Maison, bâtiment public, architecture traditionnelle, architecture contemporaine, construction en dent creuse, matériau, jardins privés');
INSERT INTO axe_thematic(axe_thematic_nom, axe_thematic_desc) VALUES ('Publicité', 'Observer la densité des enseignes, pré-enseignes et  publicités et leur positionnement, dans un objectif d’une meilleure lisibilité du territoire. Mise en place des signalisations d’information locale. Publicité, enseigne, pré-enseigne, signalisation d’information locale, infrastructure, zone d’activités,  production locale');
INSERT INTO axe_thematic(axe_thematic_nom, axe_thematic_desc) VALUES ('Entrées de bourgs/villes', 'Observer comment les entrées de ville participent à l’image et à l’identité du territoire traversé, ainsi que la lisibilité des lisières urbaines. Lisière urbaine, infrastructures');
INSERT INTO axe_thematic(axe_thematic_nom, axe_thematic_desc) VALUES ('Equipements techniques', 'Observer l’intégration des équipements dans les paysages, par la qualité de leur implantation et de leur architecture. Stations d’épuration, transformateurs électriques, captages');
INSERT INTO axe_thematic(axe_thematic_nom, axe_thematic_desc) VALUES ('Cours d’eau dans l’organisation urbaine', 'Observer la place donnée aux cours d’eau dans la construction urbaine, à la fois axe structurant historique et espace de nature en ville. Façade fluviale, espace public, nature en ville, aménagement paysager');
INSERT INTO axe_thematic(axe_thematic_nom, axe_thematic_desc) VALUES ('Ouverture/fermeture des milieux', 'Observer les phénomènes de déprise agricole qui aboutissent à une fermeture des paysages par la végétation. De nouvelles pratiques locales peuvent être mises en œuvre si le choix est fait de maintenir ou retrouver une ouverture des paysages. pelouses sèches, pâturages, landes, chemins, vallée, versant, coteaux');
INSERT INTO axe_thematic(axe_thematic_nom, axe_thematic_desc) VALUES ('Eléments structurants des paysages ruraux', 'Observer les éléments structurants des paysages ruraux qui participent d’une identité territoriale, liée souvent à l’histoire agricole des lieux. Ces éléments participent d’une qualité paysagère en évitant les phénomènes de banalisation souvent l’œuvre de pratiques agricoles intensives et en apportant des repères visuels qui peuvent être perçus depuis des vues lointaines. Murets, bocage, terrasses, clapas, captage, chemins, alignements d’arbres, bosquets, arbres symboliques');
INSERT INTO axe_thematic(axe_thematic_nom, axe_thematic_desc) VALUES ('Exploitation sylvicole', 'Observer les modes d’exploitation agricole, la gestion des coupes et des replantations. Résineux, châtaigneraie, coupes, replantations');
INSERT INTO axe_thematic(axe_thematic_nom, axe_thematic_desc) VALUES ('Diversité des cultures', 'Observer le maillage parcellaire, et la diversité des cultures qu’il convient de préserver comme un atout majeur des paysages. Cette mosaïque agricole s’inscrit dans une évolution continuelle, actuellement marquée par de nouvelles fonctions et attentes rurales, développement de loisirs, valorisation de l’environnement et du cadre de vie. Oliveraie, châtaigneraies, vignes, polyculture, peupleraies, maïs, populiculture, prairies, pâturage, verger, paysages reliques');
INSERT INTO axe_thematic(axe_thematic_nom, axe_thematic_desc) VALUES ('Bâti agricole', 'Observer la qualité de l’architecture, et de l’implantation des bâtiments agricoles. Dans les paysages ruraux, ils sont perçus depuis des vues lointaines et participent fortement de l’image de l’économie des territoires. nouveau bâtiment, friches, matériau, terrassement');
INSERT INTO axe_thematic(axe_thematic_nom, axe_thematic_desc) VALUES ('Érosion des sols', 'Observer les phénomènes d’érosion des berges des cours d’eau ou des terres agricoles, révélateurs de pratiques locales. Falaises, berges, terres agricoles, terrasses');
INSERT INTO axe_thematic(axe_thematic_nom, axe_thematic_desc) VALUES ('Dispositifs d’irrigation', 'Observer les dispositifs mis en place pour répondre aux besoins des cultures, leur intégration, et leurs évolutions vers des éléments structurants des territoires. Canaux, retenues collinaires');
INSERT INTO axe_thematic(axe_thematic_nom, axe_thematic_desc) VALUES ('Dépôts de déchets', 'Observer les lieux de décharge souvent présents dans les milieux naturels et qui dégradent l’image de ces paysages. Décharge, qualité des sols, qualité de l’eau, décharges sauvage, points d’apports volontaires');
INSERT INTO axe_thematic(axe_thematic_nom, axe_thematic_desc) VALUES ('Milieux aquatiques et zones humides', 'Observer la qualité des milieux aquatiques et des zones humides qui participent de la diversité paysagère, de la qualité du cadre de vie et du développement de la biodiversité. cours d’eau, bras mort, prairies humides, tourbières, plantes invasives, faune');
INSERT INTO axe_thematic(axe_thematic_nom, axe_thematic_desc) VALUES ('Dynamique fluviale', 'Observer l’évolution géomorphologique des cours d’eau en évolution continuelle ; cette dynamique est également le support et le révélateur de pratiques et de prise en compte des cours d’eau dans l’aménagement des territoires. Enrochement, berges, affluents, confluences, atterrissement, approfondissement du lit, crue, étiage, barrages');
INSERT INTO axe_thematic(axe_thematic_nom, axe_thematic_desc) VALUES ('Forêts', 'Observer l’évolution et la qualité des forêts dites « naturelles », qui ne font pas l’objet d’une exploitation économique, mais sont le support d’une économie touristique, ainsi qu’un élément majeur du cadre de vie des populations et de l’identité des territoires. Lisière, vieilles forêts, peuplements, chemin, desserte');
INSERT INTO axe_thematic(axe_thematic_nom, axe_thematic_desc) VALUES ('Ripisylves', 'Observer la densité et la qualité des ripisylves, et notamment la diversité des essences en place souvent mise à mal par les plantes invasives. Des percées visuelles doivent également être ménagées pour favoriser l’accessibilité au moins visuelle des cours d’eau qui participent fortement à l’identité des territoires. Plantes invasives, saulaies, espèces, ouvertures visuelles');
INSERT INTO axe_thematic(axe_thematic_nom, axe_thematic_desc) VALUES ('Énergies renouvelables', 'Observer l’implantation, la forme et la composition paysagère des équipements d’énergie renouvelable. Ces éléments doivent être observés dans leur ensemble, comme réponse à une politique énergétique locale, en rapport avec le territoire. Éoliennes, panneaux photovoltaïques, centrales hydroélectriques, méthanisation, aménagements paysagers');
INSERT INTO axe_thematic(axe_thematic_nom, axe_thematic_desc) VALUES ('Transports d’énergie', 'Observer les équipements et les réseaux liés au transport de l’énergie, pour suivre l’impact des installations et les démarches d’enfouissement. Éclairages publics, pylônes, réseaux filaires');
INSERT INTO axe_thematic(axe_thematic_nom, axe_thematic_desc) VALUES ('Niveau enneigement', 'Observer et suivre l’évolution du niveau d’enneigement et de la fonte des glaciers. Glaciers, névés, stations de ski');
INSERT INTO axe_thematic(axe_thematic_nom, axe_thematic_desc) VALUES ('Événements climatiques', 'Mutations liées aux problèmes sanitaires, reconstruction d’un milieu suite à un événement naturel dévastateur. Incendies, inondations, tempête, dépérissement,');
INSERT INTO axe_thematic(axe_thematic_nom, axe_thematic_desc) VALUES ('Carrières et gravières', 'Observer à la fois l’intégration des carrières et gravières en cours d’activité, la remise en état en fin d’exploitation, et l’évolution des paysages qui sera induite par la création de nouvelles installations, notamment dans les secteurs identifiés par le schéma de carrière. Carrière, gravière, en activité, réhabilitation');
INSERT INTO axe_thematic(axe_thematic_nom, axe_thematic_desc) VALUES ('Stationnements et flux', 'Observer l’organisation des flux touristiques, l’articulation avec les grands itinéraires routiers et notamment les espaces de stationnement proposés, dans un objectif d’équilibre entre accessibilité et préservation patrimoniale. Stationnement, infrastructures, mobilier urbain, aménagements paysagers');
INSERT INTO axe_thematic(axe_thematic_nom, axe_thematic_desc) VALUES ('Panoramas', 'Observer les lieux qui offre des vues panoramiques sur les paysages dans un objectif de maintien des cônes de vue et de valorisation touristique. Covisibilité, accès, mobilier urbain, ouverture des paysages, privatisation, aménagements paysagers');
INSERT INTO axe_thematic(axe_thematic_nom, axe_thematic_desc) VALUES ('Pratiques fluviales', 'Observer l’évolution des pratiques fluviales, comme mode de découverte des territoires et renouveau des paysages des voies d’eau. Navigation, baignade, anciennes pratiques');
INSERT INTO axe_thematic(axe_thematic_nom, axe_thematic_desc) VALUES ('Cheminements doux', 'Observer les itinéraires et la fréquentation des cheminements piétons et vélos développés dans le cadre d’une politique touristique. Chemin, espace public, aménagements paysagers, mobilier urbain, accès, visibilité');
INSERT INTO axe_thematic(axe_thematic_nom, axe_thematic_desc) VALUES ('Dispositifs d’information', 'Observer les moyens d’informations mis à disposition à la fois en termes de signalétique et d’information pédagogique. Panneaux, signalétique');
INSERT INTO axe_thematic(axe_thematic_nom, axe_thematic_desc) VALUES ('Lieux d’accueil, lieux de ressources', 'Observer les bâtiments d’accueil, d’information, et de culture, à la fois dans leur rôle, leur architecture et leur implantation, comme outil de valorisation du territoire. Musées, lieux d’accueil');
INSERT INTO axe_thematic(axe_thematic_nom, axe_thematic_desc) VALUES ('Pratiques événementielles', 'Observer les usages et pratiques à travers la mise en place d’événements festifs ou culturels. Fêtes, manifestation artistique, ginguette, actions de sensibilisation');
INSERT INTO axe_thematic(axe_thematic_nom, axe_thematic_desc) VALUES ('Zones et infrastructures de loisirs', 'Observer la qualité de l’implantation et du traitement paysager des infrastructures de loisirs, ainsi que les accès et la gestion de la fréquentation pour favoriser les loisirs durables et le tourisme intégré, en lien avec l’identité du territoire. Stations de ski, bases de loisirs, campings, équipement sportifs, plage');
INSERT INTO axe_thematic(axe_thematic_nom, axe_thematic_desc) VALUES ('Lien entre le canal latéral et la Garonne', 'Observer les liens visuels et fonctionnels entre deux éléments patrimoniaux que sont le canal latéral avec sa voie verte et la Garonne. chemins, points de vue, espaces publics, ports, écluses, moulins, voie verte');
INSERT INTO axe_thematic(axe_thematic_nom, axe_thematic_desc) VALUES ('Sécurité du randonneur', 'Signalisation d''un passage routier dangereux pour le randonneur. Conflit entre la voiture et le marcheur. Routes départementales, virage sans visibilité, travaux d’aménagement routier');
INSERT INTO axe_thematic(axe_thematic_nom, axe_thematic_desc) VALUES ('Eléments de symbolisation du pèlerinage', 'Traces laissées par les marcheurs, calvaires, croix, signes….');
INSERT INTO axe_thematic(axe_thematic_nom, axe_thematic_desc) VALUES ('Patrimoine rural', 'Observer les éléments patrimoniaux bâtis liés à l’activité agricole traditionnelle dans un objectif de préservation, de valorisation et d’intégration dans les aménagements récents. Bâtiments anciens, granges, orris, cabane, pierre sèche, terrasse, hameaux, maisons de village, caselle');
INSERT INTO axe_thematic(axe_thematic_nom, axe_thematic_desc) VALUES ('Patrimoine lié à l''eau', 'Observer les éléments patrimoniaux liés à l’eau dans un objectif de préservation, de valorisation et d’intégration dans les aménagements récents. Lavoir, puits, sources, fontaines, abreuvoirs, lacs, cascades');
INSERT INTO axe_thematic(axe_thematic_nom, axe_thematic_desc) VALUES ('Patrimoine industriel', 'Observer les éléments patrimoniaux liés à l’activité industrielle des  XIXème et XXème siècles, dans un objectif de préservation, de valorisation et d’intégration dans les aménagements récents. Bâtiments anciens, sites miniers, usines hydroélectriques, filatures');
INSERT INTO axe_thematic(axe_thematic_nom, axe_thematic_desc) VALUES ('Patrimoine historique', 'Observer les éléments patrimoniaux relatifs à l’histoire, dans un objectif de préservation, de valorisation et d’intégration dans les aménagements récents. Site historique, château, église, dolmens');
INSERT INTO axe_thematic(axe_thematic_nom, axe_thematic_desc) VALUES ('Patrimoine fluvial', 'Observer les éléments patrimoniaux liés aux pratiques et à la dynamique fluviale, dans un objectif de préservation, de valorisation et d’intégration dans les aménagements récents. Pont, écluse, quai, moulin, digue, échelle de crue, ponton, port, cale, façade fluviale, canal');
INSERT INTO axe_thematic(axe_thematic_nom, axe_thematic_desc) VALUES ('Patrimoine inscrit à l’UNESCO', 'Observer les éléments patrimoniaux inscrits à l’Unesco, dans un objectif de préservation, de valorisation et d’intégration en covisibilité avec le territoire. Canal du Midi, Causses et Cévennes, Gavarnie, chemin de St Jacques, éléments bâtis, chemins, intérêt architectural, intérêt naturel, intérêt culturel');
INSERT INTO axe_thematic(axe_thematic_nom, axe_thematic_desc) VALUES ('Sites protégés', 'Observer les éléments patrimoniaux protégés dans un objectif de préservation, de valorisation et d’intégration en covisibilité avec le territoire. Site classé, site inscrit, monument historique, intérêt architectural, intérêt naturel, intérêt culturel');
INSERT INTO axe_thematic(axe_thematic_nom, axe_thematic_desc) VALUES ('Patrimoine céleste', 'Observer le ciel nocturne comme élément patrimonial révélé selon le niveau d’éclairage urbain.');
INSERT INTO axe_thematic(axe_thematic_nom, axe_thematic_desc) VALUES ('Patrimoine géologique', 'Observer les éléments du patrimoine géologique, dans un objectif de préservation, de valorisation et d’intégration dans les aménagements récents. Surface, cavités, accès');
INSERT INTO axe_thematic(axe_thematic_nom, axe_thematic_desc) VALUES ('Patrimoine arboré', 'Alignement d’arbres, construction de parcs. Mail planté');
